import React, {Fragment, useState, useEffect} from 'react'

const Hola = (props) => {
	const [nombre, setNombre] = useState('RadicalEd')
	useEffect( () => {
		setTimeout( () => {
			setNombre('Goku')
		}, 2000)
	})
    return (
	    <Fragment>
			<h2>{props.nombre}</h2>
			{nombre}
	    </Fragment>
    )
}

export default Hola
