import React, {Fragment, useState} from 'react'

const Counter = () => {
    const [number, setNumber] = useState(0)

    const Aumentar = () => {
	    setNumber(number + 1)
    }

    const Disminuir = () => {
	    if (number > 0) {
                setNumber(number - 1)
	    }
    }

    return (
	    <Fragment>
		<h2>Valor: {number}</h2>
		<button onClick={Aumentar}>Aumentar</button>
		<button onClick={Disminuir}>Disminuir</button>
	    </Fragment>
    )
}

export default Counter
